mapper.select:
SELECT ID, USER_NAME, USER_EMAIL, USER_PASSWORD
FROM TBL_USER
WHERE USER_EMAIL = #{userEmail} AND USER_PASSWORD = #{userPassword}

SELECT ID, USER_NAME, USER_EMAIL, USER_PASSWORD
FROM TBL_USER
WHERE ID=#{id}

mapper.update:
UPDATE TBL_USER
SET USER_EMAIL = #{userEmail}, USER_PASSWORD = #{userPassword}, USER_NAME = #{userName}
WHERE ID = #{id}
